mapper.select:
	SELECT 
			TAIRI.ID,
			TAIRI.REFID,
			TAIRI.AUTHREFTYPE,
			TAIRI.AUTHID AS authItem_id,
			TAIRI.AUTHTYPE AS authItem_authType,
			TAIRI.SYSTEMID AS authItem_systemId,
			TAIRI.VALIDDEPENDENDDATE,
			TAIRI.CREATEOPERID,
			TAIRI.ENDDATE,
			TAIRI.CREATEDATE
	  FROM AUTH_AUTHREF${tableSuffix} TAIRI
	 WHERE
	
		  
            AND TAIRI.id = #{authItemRef.refId}
        
          
            AND TAIRI.REFID = #{authItemRef.refId}
		    AND TAIRI.AUTHREFTYPE = #{authItemRef.authRefType} 
		    AND TAIRI.AUTHID = #{authItemRef.authItem.id}
		    AND TAIRI.SYSTEMID = #{authItemRef.authItem.systemId}
		      
	            AND TAIRI.AUTHTYPE = #{authItemRef.authItem.authType}

	SELECT 
			TAIRI.ID,
			TAIRI.REFID,
			TAIRI.AUTHREFTYPE,
			TAIRI.AUTHID AS authItem_id,
			TAIRI.AUTHTYPE AS authItem_authType,
			TAIRI.SYSTEMID AS authItem_systemId,
			TAIRI.VALIDDEPENDENDDATE,
			TAIRI.CREATEOPERID,
			TAIRI.ENDDATE,
			TAIRI.CREATEDATE
	  FROM AUTH_AUTHREF${tableSuffix} TAIRI
	
		
			AND TAIRI.ID = #{id,javaType=java.lang.String}
		
		
			AND TAIRI.AUTHTYPE = #{authType,javaType=java.lang.String}
		
		
			AND TAIRI.SYSTEMID = #{systemId,javaType=java.lang.String}
		
		
			AND TAIRI.AUTHID = #{authItemId,javaType=java.lang.String}
		
		
			AND
			
				(TAIRI.AUTHREFTYPE = #{entryTemp.key} AND TAIRI.REFID = #{entryTemp.value})
			
        
		  
            AND TAIRI.AUTHREFTYPE = #{authRefType,javaType=java.lang.String}
        
		  
            AND TAIRI.REFID = #{refId,javaType=java.lang.String}
        
		
			  
	            AND TAIRI.AUTHID = #{authItem.id,javaType=java.lang.String}
	        
	          
	            AND TAIRI.SYSTEMID = #{authItem.systemId,javaType=java.lang.String}
	        
	          
	            AND TAIRI.AUTHTYPE = #{authItem.authType,javaType=java.lang.String}
	        
        
	
	  
          
            ORDER BY #{orderSql}
        
          
            ORDER BY AUTHREFTYPE,AUTHID

	SELECT COUNT(1)
	  FROM AUTH_AUTHREF${tableSuffix} TAIRI
	
		
			AND TAIRI.ID = #{id,javaType=java.lang.String}
		
		
			AND TAIRI.AUTHTYPE = #{authType,javaType=java.lang.String}
		
		
			AND TAIRI.SYSTEMID = #{systemId,javaType=java.lang.String}
		
		
			AND TAIRI.AUTHID = #{authItemId,javaType=java.lang.String}
		
		
			AND
			
				(TAIRI.AUTHREFTYPE = #{entryTemp.key} AND TAIRI.REFID = #{entryTemp.value})
			
        
		  
            AND TAIRI.AUTHREFTYPE = #{authRefType,javaType=java.lang.String}
        
		  
            AND TAIRI.REFID = #{refId,javaType=java.lang.String}
        
		
			  
	            AND TAIRI.AUTHID = #{authItem.id,javaType=java.lang.String}
	        
	          
	            AND TAIRI.SYSTEMID = #{authItem.systemId,javaType=java.lang.String}
	        
	          
	            AND TAIRI.AUTHTYPE = #{authItem.authType,javaType=java.lang.String}

mapper.delete:
	DELETE FROM AUTH_AUTHREF${tableSuffix} WHERE
	
		  
            AND REFID = #{authItemRef.refId}
        
          
            AND AUTHREFTYPE = #{authItemRef.authRefType} 
        
        
			  
	            AND AUTHID = #{authItemRef.authItem.id}
	        
	          
	            AND AUTHTYPE = #{authItemRef.authItem.authType,javaType=java.lang.String}
	        
	          
	            AND SYSTEMID = #{authItemRef.authItem.systemId,javaType=java.lang.String}

mapper.update:
  
	    UPDATE AUTH_AUTHREF${tableSuffix} TAIRI
	    
			
	    		VALIDDEPENDENDDATE = #{validDependEndDate,javaType=boolean},
	    	
			
	    		ENDDATE = #{endDate,javaType=java.util.Date},
	    	
	    
	    WHERE
		
			  
	            AND TAIRI.id = #{authItemRef.refId}
	        
	          
	            AND TAIRI.REFID = #{authItemRef.refId}
			    AND TAIRI.AUTHREFTYPE = #{authItemRef.authRefType} 
			    AND TAIRI.AUTHID = #{authItemRef.authItem.id}
			    AND TAIRI.SYSTEMID = #{authItemRef.authItem.systemId}
			    AND TAIRI.AUTHTYPE = #{authItemRef.authItem.authType}
