mapper.select:
	SELECT 
			TJSK.id,
			TJSK.type,
			TJSK.signingKeyCode,
			TJSK.signingKey,
			TJSK.createDate,
			TJSK.duration
	  FROM sec_jwt_signing_key TJSK
	
		  
            AND TJSK.id = #{id,javaType=java.lang.String}
        
		  
            AND TJSK.type = #{type,javaType=java.lang.String}
        
		  
            AND TJSK.signingKeyCode = #{signingKeyCode,javaType=java.lang.String}
        
		  
            AND TJSK.signingKey = #{signingKey,javaType=java.lang.String}
        
		  
            AND TJSK.createDate = #{createDate,javaType=java.util.Date}
        
		  
            AND TJSK.duration = #{duration,javaType=long}
        
	
	  
          
            ORDER BY ${orderSql}
        
          
            ORDER BY ID

	SELECT COUNT(1)
	  FROM sec_jwt_signing_key TJSK
	
		  
            AND TJSK.id = #{id,javaType=java.lang.String}
        
		  
            AND TJSK.type = #{type,javaType=java.lang.String}
        
		  
            AND TJSK.signingKeyCode = #{signingKeyCode,javaType=java.lang.String}
        
		  
            AND TJSK.signingKey = #{signingKey,javaType=java.lang.String}
        
		  
            AND TJSK.createDate = #{createDate,javaType=java.util.Date}
        
		  
            AND TJSK.duration = #{duration,javaType=long}
