mapper.select:
SELECT TBDT.id, TBDT.module, TBDT.valid, TBDT.modifyAble, TBDT.common, TBDT.viewType, TBDT.code, TBDT.createDate, TBDT.lastUpdateDate, TBDT.name, TBDT.tableName, TBDT.remark FROM BD_BASIC_DATA_TYPE TBDT WHERE
AND TBDT.ID = #{id}
AND TBDT.CODE = #{code,jdbcType=VARBINARY}

	SELECT 
			TBDT.id,
			TBDT.module,
			TBDT.valid,
			TBDT.modifyAble,
			TBDT.common,
			TBDT.viewType,
			TBDT.code,
			TBDT.createDate,
			TBDT.lastUpdateDate,
			TBDT.name,
			TBDT.tableName,
			TBDT.remark
	  FROM BD_BASIC_DATA_TYPE TBDT
	
		  
             AND TBDT.MODIFYABLE = #{modifyAble,jdbcType=BIT} 
        
          
             AND TBDT.VALID = #{valid,jdbcType=BIT} 
        
          
             AND TBDT.COMMON = #{common,jdbcType=BIT} 
        
		  
             AND TBDT.MODULE = #{module,jdbcType=VARCHAR} 
        
		  
             AND TBDT.CODE = #{code,jdbcType=VARCHAR} 
        
          
             AND TBDT.VIEWTYPE = #{viewType,jdbcType=VARBINARY} 
        
		  
             AND TBDT.TABLENAME = #{tableName,jdbcType=VARCHAR} 
        
		  
             AND TBDT.NAME = #{name,jdbcType=VARCHAR} 
        
		
			 AND TBDT.ID <> #{excludeId,javaType=java.lang.String} 
		
	
	  
          
            ORDER BY ${orderSql}
        
          
            ORDER BY ID

	SELECT COUNT(1)
	  FROM BD_BASIC_DATA_TYPE TBDT
	
		  
             AND TBDT.MODIFYABLE = #{modifyAble,jdbcType=BIT} 
        
          
             AND TBDT.VALID = #{valid,jdbcType=BIT} 
        
          
             AND TBDT.COMMON = #{common,jdbcType=BIT} 
        
		  
             AND TBDT.MODULE = #{module,jdbcType=VARCHAR} 
        
		  
             AND TBDT.CODE = #{code,jdbcType=VARCHAR} 
        
          
             AND TBDT.VIEWTYPE = #{viewType,jdbcType=VARBINARY} 
        
		  
             AND TBDT.TABLENAME = #{tableName,jdbcType=VARCHAR} 
        
		  
             AND TBDT.NAME = #{name,jdbcType=VARCHAR} 
        
		
			 AND TBDT.ID <> #{excludeId,javaType=java.lang.String}

mapper.delete:
	DELETE FROM BD_BASIC_DATA_TYPE WHERE
	
		  
            AND ID = #{id}
        
          
             AND CODE = #{code,jdbcType=VARBINARY}
