mapper.select:
SELECT TDD.id, TDD.parentId, TDD.type, TDD.code, TDD.createDate, TDD.lastUpdateDate, TDD.valid, TDD.modifyAble, TDD.name, TDD.remark FROM BD_DATA_DICT TDD WHERE
AND TDD.ID = #{id}
AND TDD.type = #{type,jdbcType=VARCHAR}
AND TDD.CODE = #{code,jdbcType=VARCHAR}

	SELECT 
			TDD.id,
			TDD.parentId,
			TDD.type,
			TDD.code,
			TDD.createDate,
			TDD.lastUpdateDate,
			TDD.valid,
			TDD.modifyAble,
			TDD.name,
			TDD.remark
	  FROM BD_DATA_DICT TDD
	
		  
             AND TDD.TYPE = #{type,jdbcType=VARCHAR} 
        
          
             AND TDD.PARENTID = #{parentId,jdbcType=VARCHAR} 
        
          
             AND TDD.CODE = #{code,jdbcType=VARCHAR} 
        
		  
             AND TDD.MODIFYABLE = #{modifyAble,jdbcType=BIT} 
        
		  
             AND TDD.VALID = #{valid,jdbcType=BIT} 
        
		  
             AND TDD.REMARK = #{remark,jdbcType=VARCHAR} 
        
		  
             AND TDD.NAME = #{name,jdbcType=VARCHAR} 
        
		
			 AND TDD.ID <> #{excludeId,javaType=java.lang.String} 
		
	
	  
          
            ORDER BY ${orderSql}
        
          
            ORDER BY ID

	SELECT COUNT(1)
	  FROM BD_DATA_DICT TDD
	
		  
             AND TDD.TYPE = #{type,jdbcType=VARCHAR} 
        
          
             AND TDD.PARENTID = #{parentId,jdbcType=VARCHAR} 
        
          
             AND TDD.CODE = #{code,jdbcType=VARCHAR} 
        
		  
             AND TDD.MODIFYABLE = #{modifyAble,jdbcType=BIT} 
        
		  
             AND TDD.VALID = #{valid,jdbcType=BIT} 
        
		  
             AND TDD.REMARK = #{remark,jdbcType=VARCHAR} 
        
		  
             AND TDD.NAME = #{name,jdbcType=VARCHAR} 
        
		
			 AND TDD.ID <> #{excludeId,javaType=java.lang.String}

mapper.delete:
	DELETE FROM BD_DATA_DICT WHERE
	
		  
            AND ID = #{id}
        
		  
            AND PARENTID = #{parentId}
        
          
            AND CODE = #{code,jdbcType=VARCHAR}
        
          
            AND TYPE = #{type,jdbcType=VARCHAR}
