report.script:
function timeUnitFn(cellvalue,options,index){
        if($.ObjectUtils.isEmpty(cellvalue)){
        return cellvalue;
        }
        var time=options['time'];
        var unitName ="";
        if(cellvalue =='DAY'){
            unitName = "天"
        }else  if(cellvalue =='MONTH'){
            unitName = "月";
            }else  if(cellvalue =='YEAR'){
            unitName = "年"
            }
        return time +" "+ unitName;
}



function showInvestProjectDetail(value,index){
  var row =  grid.datagrid('getSelected')
    var url =	$.formatString(_contextPath+"/investProject/toInvestProjectTabs.action?status=INVEST_EFFICTIVE&investProjectId={0}",row.investProjectId);
    $.triggerge("addOrSelectTab", {
    title : '[投资项目:]'+value,
    href : url
    });
}


function settledExpire(value,row,index){
    var text = value;
    if(!$.ObjectUtils.isEmpty(value)){
        var investProjectId = row.investProjectId;
        var now = new Date();

        if(now>row.nextSettleDate && row["status"]=="AC"){
            text= $.formatString('{0}',value  );
        }
         }
        return text;

    }

sqlMappers.sqlMapper:
SELECT DISTINCT TIAID.id, twr.productName, twr.originalVoucherCode, twri.originalVoucherCode as originalVoucherCodeitem, twr.measurementinfoid, (select name from bd_measurementinfo where id = twr.measurementinfoid) as measurementinfoname, tw.company,
twr.number, twr.unitPrice, twr.totalAmount, p.NAME AS investprojectname, p.investProjectNumber, p.amount , p.repayType, p.effectiveDate, p.expiredDate, p.timeUnit, p.time, cl.userName, cl.loginName, TIAID.investProjectId, TIAID.investAmount, TIAID.settledPeriod, TIAID.totalPeriod, TIAID.currentPeriod, TIAID.receivableAmount, TIAID.receivableIncomeAmount, TIAID.receivablePrincipalAmount, TIAID.nextSettleDate, TIAID.`status`, tiis.settleProcessDate, cp.username as rzr FROM IA_INVEST_ACCOUNT_ITEM TIAID LEFT JOIN cl_client_info cl ON TIAID.clientId = cl.id LEFT JOIN inv_invest_project p ON TIAID.investProjectId = p.id left join cl_client_info cp on p.clientinfoId = cp.id LEFT JOIN wr_warehouse_receipt_item twri ON p.refId = twri.id LEFT JOIN wr_warehouse_receipt twr ON twri.warehouseReceiptId = twr.id LEFT JOIN wr_warehouse tw ON twr.WAREHOUSEID = tw.id LEFT JOIN ia_invest_item_settle_schedule tiis ON tiis.investAccountItemId = TIAID.id
WHERE 1=1 and TIAID.status in ('AC','FP')
AND cl.mobilePhoneNumber = #{mobilePhoneNumber,jdbcType=VARCHAR}
AND TIAID.id = #{investAccountId,jdbcType=VARCHAR}
AND cl.loginName = #{loginName,jdbcType=VARCHAR}
AND cl.userName = #{userName,jdbcType=VARCHAR}
AND TIAID.`status` = #{settleStatus,jdbcType=VARCHAR}
AND twr.originalVoucherCode = #{originalVoucherCode,jdbcType=VARCHAR}
AND twri.originalVoucherCode = #{originalVoucherCodeitem,jdbcType=VARCHAR}
AND p.investProjectNumber = #{investProjectNumber,jdbcType=VARCHAR}
AND TIAID.nextSettleDate >= #{minNextSettleDate,jdbcType=TIMESTAMP}
AND TIAID.nextSettleDate <= #{maxNextSettleDate,jdbcType=TIMESTAMP}
AND p.effectiveDate < DATE_ADD(#{maxEffectiveDate},INTERVAL 1 DAY)
AND p.effectiveDate >= #{minEffectiveDate,jdbcType=TIMESTAMP}
AND p.expiredDate < DATE_ADD(#{maxExpiredDate},INTERVAL 1 DAY)
AND p.expiredDate >= #{minExpiredDate,jdbcType=TIMESTAMP}
ORDER BY ${orderSql}
ORDER BY TIAID.`status`, nextSettleDate
